const StationSvg = styled(Svg)`
  --pos1: translate(
      calc(-50vw - var(--width) / 1.5),
      calc(25vh + var(--height) / 1.75)
    )
    rotate(315deg);

  --pos2: translate(
      calc(10vw + var(--width) / 2),
      calc(-50vh - var(--height) / 1.75)
    )
    rotate(315deg);

  transform: var(--pos1);

  @keyframes fly_station {
    0% {
      transform: var(--pos1);
    }
    ${timings[4].keyframes[0]}${"%"} {
      transform: var(--pos1);
    }
    ${timings[4].keyframes[1]}${"%"} {
      transform: var(--pos2);
    }
    100% {
      transform: var(--pos2);
    }
  }

  @media (${BREAKPOINTS.mdAndSmaller}) {
    display: none;
  }

  animation: ${timings[4].duration + 5 + 6}s linear ${timings[4].offset}s
    infinite fly_station;
`;

